private static char decodeHexNibble(final char c) {
  if ('0' <= c && c <= '9') {
    return (char) (c - '0');
  } else if ('a' <= c && c <= 'f') {
    return (char) (c - 'a' + 10);
  } else if ('A' <= c && c <= 'F') {
    return (char) (c - 'A' + 10);
  } else {
    return Character.MAX_VALUE;
  }
}
