private static void sendResponseAndClose(ChannelHandlerContext ctx,
    DefaultHttpResponse resp) {
  resp.headers().set(CONNECTION, CLOSE);
  ctx.writeAndFlush(resp).addListener(ChannelFutureListener.CLOSE);
}
