public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
  LOG.info("Proxy for " + uri + " failed. cause: ", cause);
  if (proxiedChannel != null) {
    proxiedChannel.close();
    proxiedChannel = null;
  }
  ctx.close();
}
