private long getLeastAvailableSpace() {
  long leastAvailable = Long.MAX_VALUE;
  for (AvailableSpaceVolumePair volume : volumes) {
    leastAvailable = Math.min(leastAvailable, volume.getAvailable());
  }
  return leastAvailable;
}
