public void close() throws IOException {
  IOException ioe = null;
  for (FsVolumeReference ref : references) {
    try {
      ref.close();
    } catch (IOException e) {
      ioe = e;
    }
  }
  references.clear();
  if (ioe != null) {
    throw ioe;
  }
}
