public void closeStreams() throws IOException {
  IOException ioe = null;
  if(checksumIn!=null) {
    try {
      checksumIn.close(); // close checksum file
    } catch (IOException e) {
      ioe = e;
    }
    checksumIn = null;
  }
  if(dataIn!=null) {
    try {
      dataIn.close(); // close data file
    } catch (IOException e) {
      ioe = e;
    }
    dataIn = null;
    dataInFd = null;
  }
  if (volumeRef != null) {
    IOUtils.cleanup(null, volumeRef);
    volumeRef = null;
  }
  // throw IOException if there is any
  if(ioe!= null) {
    throw ioe;
  }
}
