boolean checkVolumesRemoved() {
  Iterator<FsVolumeImpl> it = volumesBeingRemoved.iterator();
  while (it.hasNext()) {
    FsVolumeImpl volume = it.next();
    if (!volume.checkClosed()) {
      return false;
    }
    it.remove();
  }
  return true;
}
