private ProvidedBlockPoolSlice getProvidedBlockPoolSlice(String bpid)
    throws IOException {
  ProvidedBlockPoolSlice bp = bpSlices.get(bpid);
  if (bp == null) {
    throw new IOException("block pool " + bpid + " is not found");
  }
  return bp;
}
