void shutdownBlockPool(String bpid, BlockListAsLongs blocksListsAsLongs) {
  ProvidedBlockPoolSlice bp = bpSlices.get(bpid);
  if (bp != null) {
    bp.shutdown(blocksListsAsLongs);
  }
  bpSlices.remove(bpid);
}
