private static URI getAbsoluteURI(URI uri) {
  if (!uri.isAbsolute()) {
    // URI is not absolute implies it is for a local file
    // normalize the URI
    return StorageLocation.normalizeFileURI(uri);
  } else {
    return uri;
  }
}
