private int fillBuffer(FileChannel channel, ByteBuffer buf)
    throws IOException {
  int bytesRead = channel.read(buf);
  if (bytesRead < 0) {
    //EOF
    return bytesRead;
  }
  while (buf.remaining() > 0) {
    int n = channel.read(buf);
    if (n < 0) {
      //EOF
      return bytesRead;
    }
    bytesRead += n;
  }
  return bytesRead;
}
