protected void compute() {
  try {
    addToReplicasMap(volumeMap, dir, lazyWriteReplicaMap, isFinalized,
        exceptions, subTaskQueue);
  } catch (IOException e) {
    LOG.warn("Caught exception while adding replicas from " + volume
        + " in subtask. Will throw later.", e);
    exceptions.add(e);
  }
}
