long reserve(long bytesCount) {
  while (true) {
    long cur = usedBytes.get();
    long next = cur + bytesCount;
    if (next > maxBytes) {
      return -1;
    }
    if (usedBytes.compareAndSet(cur, next)) {
      return next;
    }
  }
}

