void cleanup() {
  // Remove all files under the volume.
  for (String pmemDir: pmemVolumes) {
    try {
      FileUtils.cleanDirectory(new File(pmemDir));
    } catch (IOException e) {
      LOG.error("Failed to clean up " + pmemDir, e);
    }
  }
}
