public void reserveSpaceForRbw(long bytesToReserve) {
  if (bytesToReserve != 0) {
    if (FsDatasetImpl.LOG.isDebugEnabled()) {
      FsDatasetImpl.LOG.debug("Reserving " + bytesToReserve + " on volume " + getBasePath());
    }
    reservedForRbw.addAndGet(bytesToReserve);
  }
}
