public static String nextSorted(List<String> arr, String prev) {
  int res = 0;
  if (prev != null) {
    res = Collections.binarySearch(arr, prev);
    if (res < 0) {
      res = -1 - res;
    } else {
      res++;
    }
  }
  if (res >= arr.size()) {
    return null;
  }
  return arr.get(res);
}
