public void removeDeletedBlocks(String bpid, Set<Long> blockIds) {
  synchronized (deletingBlock) {
    Set<Long> s = deletingBlock.get(bpid);
    if (s != null) {
      for (Long id : blockIds) {
        s.remove(id);
      }
    }
  }
}
