private void addDeletingBlock(String bpid, Long blockId) {
  synchronized(deletingBlock) {
    Set<Long> s = deletingBlock.get(bpid);
    if (s == null) {
      s = new HashSet<Long>();
      deletingBlock.put(bpid, s);
    }
    s.add(blockId);
  }
}
