void trySendErrorReport(String bpid, int errCode, String errMsg) {
  BPOfferService bpos = blockPoolManager.get(bpid);
  if (bpos == null) {
    throw new IllegalArgumentException("Bad block pool: " + bpid);
  }
  bpos.trySendErrorReport(errCode, errMsg);
}
