synchronized void checkDatanodeUuid() throws IOException {
  if (storage.getDatanodeUuid() == null) {
    storage.setDatanodeUuid(generateUuid());
    storage.writeAll();
    LOG.info("Generated and persisted new Datanode UUID {}",
        storage.getDatanodeUuid());
  }
}
