public static DataNode createDataNode(String args[], Configuration conf,
    SecureResources resources) throws IOException {
  DataNode dn = instantiateDataNode(args, conf, resources);
  if (dn != null) {
    dn.runDatanodeDaemon();
  }
  return dn;
}
