static long getLong(Configuration conf, String key, long defaultValue) {
  final long v = conf.getLong(key, defaultValue);
  LOG.info(key + " = " + v + " (default=" + defaultValue + ")");
  if (v <= 0) {
    throw new HadoopIllegalArgumentException(key + " = " + v  + " <= " + 0);
  }
  return v;
}
