synchronized void stopMaintenance(final DatanodeDescriptor node) {
  LOG.info("Stopping maintenance of {} node {}",
      node.isAlive() ? "live" : "dead", node);
  if (!node.isAlive()) {
    node.stopMaintenance();
  } else {
    stats.subtract(node);
    node.stopMaintenance();
    stats.add(node);
  }
}
