protected int compareDataNode(final DatanodeDescriptor a,
    final DatanodeDescriptor b) {
  if (a.equals(b)
      || Math.abs(a.getDfsUsedPercent() - b.getDfsUsedPercent()) < 5) {
    return 0;
  }
  return a.getDfsUsedPercent() < b.getDfsUsedPercent() ? -1 : 1;
}
