public void appendUCPartsConcise(StringBuilder sb) {
  sb.append("replicas=");
  int i = 0;
  for (ReplicaUnderConstruction r : replicas) {
    sb.append(r.getExpectedStorageLocation().getDatanodeDescriptor());
    if (++i < replicas.length) {
      sb.append(", ");
    }
  }
}
