private void incrementVersionCount(String version) {
  if (version == null) {
    return;
  }
  synchronized(this) {
    Integer count = this.datanodesSoftwareVersions.get(version);
    count = count == null ? 1 : count + 1;
    this.datanodesSoftwareVersions.put(version, count);
  }
}
