void resolveUpgradeDomain(DatanodeDescriptor node) {
  String upgradeDomain = hostConfigManager.getUpgradeDomain(node);
  if (upgradeDomain != null && upgradeDomain.length() > 0) {
    node.setUpgradeDomain(upgradeDomain);
  }
}
