public double getInServiceXceiverAverage() {
  double avgLoad = 0;
  final int nodes = getNumDatanodesInService();
  if (nodes != 0) {
    final int xceivers = datanodeStatistics.getInServiceXceiverCount();
    avgLoad = (double)xceivers/nodes;
  }
  return avgLoad;
}
