List<DatanodeStorageInfo> getTargets(BlockInfo block) {
  synchronized (pendingReconstructions) {
    PendingBlockInfo found = pendingReconstructions.get(block);
    if (found != null) {
      return found.targets;
    }
  }
  return null;
}
