private Queue<ReportedBlockInfo> getBlockQueue(Block block) {
  Queue<ReportedBlockInfo> queue = queueByBlockId.get(block);
  if (queue == null) {
    queue = Lists.newLinkedList();
    queueByBlockId.put(block, queue);
  }
  return queue;
}
