  // for PROVIDED storage, avoid counting the same storage
  // across multiple datanodes
  if (storageType == StorageType.PROVIDED && nodesInService > 0) {
    return capacityNonDfsUsed/nodesInService;
  }
  return capacityNonDfsUsed;
}

