public BlockStoragePolicy[] getAllPolicies() {
  List<BlockStoragePolicy> list = Lists.newArrayList();
  if (policies != null) {
    for (BlockStoragePolicy policy : policies) {
      if (policy != null) {
        list.add(policy);
      }
    }
  }
  return list.toArray(new BlockStoragePolicy[list.size()]);
}
