private int findSlot() {
  int i = getTotalBlockNum();
  for (; i < getCapacity(); i++) {
    if (getStorageInfo(i) == null) {
      return i;
    }
  }
  // need to expand the triplet size
  ensureCapacity(i + 1, true);
  return i;
}
