Block getBlockOnStorage(DatanodeStorageInfo storage) {
  int index = getStorageBlockIndex(storage);
  if (index < 0) {
    return null;
  } else {
    Block block = new Block(this);
    block.setBlockId(this.getBlockId() + index);
    return block;
  }
}
