        .setJid(convertJournalId(journalId))
        .build();
    IsFormattedResponseProto resp = rpcProxy.isFormatted(
        NULL_CONTROLLER, req);
    return resp.getIsFormatted();
  } catch (ServiceException e) {
    throw ProtobufHelper.getRemoteException(e);
  }
}
