File getOrCreatePaxosDir() {
  File paxosDir = new File(sd.getCurrentDir(), "paxos");
  if(!paxosDir.exists()) {
    LOG.info("Creating paxos dir: {}", paxosDir.toPath());
    if(!paxosDir.mkdir()) {
      LOG.error("Could not create paxos dir: {}", paxosDir.toPath());
    }
  }
  return paxosDir;
}
