void stopSync() {
  shouldSync = false;
  // Delete the edits.sync directory
  File editsSyncDir = journal.getStorage().getEditsSyncDir();
  if (editsSyncDir.exists()) {
    FileUtil.fullyDelete(editsSyncDir);
  }
  if (syncJournalDaemon != null) {
    syncJournalDaemon.interrupt();
  }
}

