private boolean createEditsSyncDir() {
  File editsSyncDir = journal.getStorage().getEditsSyncDir();
  if (editsSyncDir.exists()) {
    LOG.info(editsSyncDir + " directory already exists.");
    return true;
  }
  return editsSyncDir.mkdir();
}
