public static boolean isValidNameForComponent(String component) {
  if (component.equals(".") ||
      component.equals("..") ||
      component.indexOf(":") >= 0 ||
      component.indexOf("/") >= 0) {
    return false;
  }
  return !isReservedPathComponent(component);
}
