static URI trimUri(URI uri) {
  String path = uri.getPath();
  if (HdfsConstants.HDFS_URI_SCHEME.equals(uri.getScheme()) &&
      path != null && !path.isEmpty()) {
    uri = createUri(uri.getScheme(), uri.getHost(), uri.getPort());
  }
  return uri;
}
