public Peer accept() throws IOException, SocketTimeoutException {
  DomainSocket connSock = sock.accept();
  Peer peer = null;
  boolean success = false;
  try {
    peer = new DomainPeer(connSock);
    success = true;
    return peer;
  } finally {
    if (!success) {
      if (peer != null) peer.close();
      connSock.close();
    }
  }
}
