private static Object[] toJsonArray(final StorageType[] array) {
  if (array == null) {
    return null;
  } else if (array.length == 0) {
    return EMPTY_OBJECT_ARRAY;
  } else {
    final Object[] a = new Object[array.length];
    for(int i = 0; i < array.length; i++) {
      a[i] = array[i];
    }
    return a;
  }
}
