  if (xAttr == null) {
    return null;
  }

  final Map<String, Object> m = new TreeMap<String, Object>();
  m.put("name", XAttrHelper.getPrefixedName(xAttr));
  m.put("value", xAttr.getValue() != null ?
      XAttrCodec.encodeValue(xAttr.getValue(), encoding) : null);
  return m;
}

