protected void write(String toWrite) throws IOException  {
  if(!okToWrite)
    throw new IOException("file not open for writing.");

  if(printToScreen)
    System.out.print(toWrite);

  try {
    fw.write(toWrite);
  } catch (IOException e) {
    okToWrite = false;
    throw e;
  }
}
