void addChild(String key, Node node) {
  if (children == null) {
    children = new HashMap<>();
  }
  LinkedList<Node> cur = children.get(key);
  if (cur == null) {
    cur = new LinkedList<>();
    children.put(key, cur);
  }
  cur.add(node);
}
