String removeChildStr(String key) {
  Node child = removeChild(key);
  if (child == null) {
    return null;
  }
  if ((child.children != null) && (!child.children.isEmpty())) {
    throw new RuntimeException("Node " + key + " contains children " +
        "of its own.");
  }
  return child.getVal();
}
