       if (e instanceof AccessControlException) {
         return new REMOVE3Response(Nfs3Status.NFS3ERR_PERM, dirWcc);
       } else {
         return new REMOVE3Response(Nfs3Status.NFS3ERR_IO, dirWcc);
       }
