  String op = argv[i + 2];
  if ("start".equals(op)) {
    return startReconfiguration(nodeType, address);
  } else if ("status".equals(op)) {
    return getReconfigurationStatus(nodeType, address, System.out, System.err);
  }
  System.err.println("Unknown operation: " + op);
  return -1;
}
