  if (zkfcBindAddr == null || zkfcBindAddr.isEmpty()) {
    zkfcBindAddr = localTarget.getAddress().getAddress().getHostAddress();
  }
  return new InetSocketAddress(zkfcBindAddr, zkfcPort);
}

