private void attachToParentNoBalance(Node<E> toDelete, Node<E> toConnect) {
  Node<E> parent = toDelete.parent;
  toConnect.parent = parent;
  if (parent == null) {
    root = toConnect;
  } else if (toDelete == parent.left) {
    parent.left = toConnect;
  } else {
    parent.right = toConnect;
  }
}
